// Player Statistics Interface
export interface PlayerStats {
  goals: number;
  assists: number;
  cleanSheets: number;
  yellowCards: number;
  redCards: number;
  appearances: number;
  avgRating?: number;
}

// Player Interface
export interface Player {
  id: string;
  name: string;
  club: string;
  position: 'GK' | 'DEF' | 'MID' | 'FWD';
  photo: string;
  stats: PlayerStats;
  fantasyScore?: number;
  market_value?: number;
}

// Fantasy Team Interface
export interface FantasyTeam {
  id?: string;
  name: string;
  players: Player[];
  totalScore: number;
  createdAt?: Date;
}

// Team Validation Result
export interface TeamValidationResult {
  valid: boolean;
  errors: string[];
}

// Scoring Formula Interface
export interface ScoringFormula {
  goalsMultiplier: number;
  assistsMultiplier: number;
  cleanSheetsMultiplier: number;
  yellowCardsPenalty: number;
  redCardsPenalty: number;
}

// Default scoring formula
export const DEFAULT_SCORING_FORMULA: ScoringFormula = {
  goalsMultiplier: 4,
  assistsMultiplier: 3,
  cleanSheetsMultiplier: 2,
  yellowCardsPenalty: 1,
  redCardsPenalty: 3,
};

// API Response Types
export interface ApiResponse<T> {
  success: boolean;
  data?: T;
  error?: string;
}

import { PlayerStats, ScoringFormula, DEFAULT_SCORING_FORMULA } from './types';

/**
 * Calculate fantasy score based on player statistics and scoring formula
 */
export function calculateFantasyScore(
  stats: PlayerStats,
  formula: ScoringFormula = DEFAULT_SCORING_FORMULA
): number {
  const score =
    stats.goals * formula.goalsMultiplier +
    stats.assists * formula.assistsMultiplier +
    stats.cleanSheets * formula.cleanSheetsMultiplier -
    stats.yellowCards * formula.yellowCardsPenalty -
    stats.redCards * formula.redCardsPenalty;

  return Math.max(0, score); // Never return negative scores
}

/**
 * Calculate average fantasy score per appearance
 */
export function calculateAverageScore(
  stats: PlayerStats,
  formula: ScoringFormula = DEFAULT_SCORING_FORMULA
): number {
  if (stats.appearances === 0) return 0;
  return calculateFantasyScore(stats, formula) / stats.appearances;
}

/**
 * Validate team composition
 */
export interface TeamComposition {
  goalkeepers: number;
  defenders: number;
  midfielders: number;
  forwards: number;
}

export function validateTeamComposition(
  composition: TeamComposition
): { valid: boolean; errors: string[] } {
  const errors: string[] = [];

  // Total 11 players
  const total = composition.goalkeepers + composition.defenders + composition.midfielders + composition.forwards;
  if (total !== 11) {
    errors.push(`Team must have exactly 11 players, got ${total}`);
  }

  // At least 1 goalkeeper
  if (composition.goalkeepers < 1) {
    errors.push('Team must have at least 1 goalkeeper');
  }

  // Maximum 3 goalkeepers
  if (composition.goalkeepers > 3) {
    errors.push('Team cannot have more than 3 goalkeepers');
  }

  // Between 3-6 defenders
  if (composition.defenders < 3 || composition.defenders > 6) {
    errors.push('Team must have between 3 and 6 defenders');
  }

  // Between 2-5 midfielders
  if (composition.midfielders < 2 || composition.midfielders > 5) {
    errors.push('Team must have between 2 and 5 midfielders');
  }

  // Between 1-3 forwards
  if (composition.forwards < 1 || composition.forwards > 3) {
    errors.push('Team must have between 1 and 3 forwards');
  }

  return {
    valid: errors.length === 0,
    errors,
  };
}

/**
 * Sort players by various criteria
 */
export function sortPlayers<T extends { stats: PlayerStats; fantasyScore?: number }>(
  players: T[],
  sortBy: 'fantasyScore' | 'goals' | 'assists' | 'appearances' | 'avgRating'
): T[] {
  return [...players].sort((a, b) => {
    switch (sortBy) {
      case 'fantasyScore':
        return (b.fantasyScore || 0) - (a.fantasyScore || 0);
      case 'goals':
        return b.stats.goals - a.stats.goals;
      case 'assists':
        return b.stats.assists - a.stats.assists;
      case 'appearances':
        return b.stats.appearances - a.stats.appearances;
      case 'avgRating':
        return (b.stats.avgRating || 0) - (a.stats.avgRating || 0);
      default:
        return 0;
    }
  });
}

import NodeCache from 'node-cache';

class CacheService {
  private cache: NodeCache;

  constructor() {
    // Cache with 10 minute standard TTL
    this.cache = new NodeCache({ stdTTL: 600, checkperiod: 120 });
  }

  /**
   * Get value from cache
   */
  get<T>(key: string): T | undefined {
    return this.cache.get<T>(key);
  }

  /**
   * Set value in cache
   */
  set<T>(key: string, value: T, ttl?: number): void {
    this.cache.set(key, value, ttl);
  }

  /**
   * Delete value from cache
   */
  delete(key: string): void {
    this.cache.del(key);
  }

  /**
   * Clear entire cache
   */
  clear(): void {
    this.cache.flushAll();
  }

  /**
   * Check if key exists in cache
   */
  has(key: string): boolean {
    return this.cache.has(key);
  }
}

export const cacheService = new CacheService();

import express, { Request, Response, NextFunction } from 'express';
import cors from 'cors';
import {
  Player,
  PlayerStats,
  ScoringFormula,
  DEFAULT_SCORING_FORMULA,
  ApiResponse,
} from './types';
import {
  calculateFantasyScore,
  calculateAverageScore,
  validateTeamComposition,
  TeamComposition,
  sortPlayers,
} from './scoring';
import {
  getMockPlayers,
  getMockPlayerById,
  getMockPlayersByPosition,
  getTopMockPlayers,
} from './mockData';
import { cacheService } from './cache';

const app = express();
const PORT = process.env.PORT || 5000;

// Middleware
app.use(cors());
app.use(express.json());

// Error handling middleware
app.use((err: any, req: Request, res: Response, next: NextFunction) => {
  console.error(err);
  res.status(500).json({
    success: false,
    error: 'Internal Server Error',
  });
});

// =====================
// Endpoints
// =====================

/**
 * GET /health - Health check
 */
app.get('/health', (req: Request, res: Response) => {
  res.json({ success: true, status: 'OK' });
});

/**
 * GET /players - Fetch list of all players
 * Query params: position (GK|DEF|MID|FWD), sortBy (fantasyScore|goals|assists)
 */
app.get('/players', (req: Request, res: Response) => {
  try {
    const cacheKey = `players:${JSON.stringify(req.query)}`;
    const cached = cacheService.get<ApiResponse<Player[]>>(cacheKey);
    if (cached) {
      return res.json(cached);
    }

    let players = getMockPlayers();

    // Filter by position
    if (req.query.position) {
      const position = req.query.position as string;
      if (['GK', 'DEF', 'MID', 'FWD'].includes(position)) {
        players = players.filter((p) => p.position === position);
      }
    }

    // Sort
    if (req.query.sortBy) {
      const sortBy = req.query.sortBy as string;
      if (['fantasyScore', 'goals', 'assists', 'appearances'].includes(sortBy)) {
        players = sortPlayers(
          players,
          sortBy as 'fantasyScore' | 'goals' | 'assists' | 'appearances'
        );
      }
    }

    const response: ApiResponse<Player[]> = {
      success: true,
      data: players,
    };

    cacheService.set(cacheKey, response, 600);
    res.json(response);
  } catch (error) {
    res.status(500).json({
      success: false,
      error: 'Failed to fetch players',
    });
  }
});

/**
 * GET /players/:id - Fetch single player
 */
app.get('/players/:id', (req: Request, res: Response) => {
  try {
    const cacheKey = `player:${req.params.id}`;
    const cached = cacheService.get<ApiResponse<Player>>(cacheKey);
    if (cached) {
      return res.json(cached);
    }

    const player = getMockPlayerById(req.params.id);
    if (!player) {
      return res.status(404).json({
        success: false,
        error: 'Player not found',
      });
    }

    const response: ApiResponse<Player> = {
      success: true,
      data: player,
    };

    cacheService.set(cacheKey, response, 600);
    res.json(response);
  } catch (error) {
    res.status(500).json({
      success: false,
      error: 'Failed to fetch player',
    });
  }
});

/**
 * GET /players/:id/stats - Fetch detailed player stats
 */
app.get('/players/:id/stats', (req: Request, res: Response) => {
  try {
    const cacheKey = `player:stats:${req.params.id}`;
    const cached = cacheService.get<ApiResponse<PlayerStats>>(cacheKey);
    if (cached) {
      return res.json(cached);
    }

    const player = getMockPlayerById(req.params.id);
    if (!player) {
      return res.status(404).json({
        success: false,
        error: 'Player not found',
      });
    }

    const response: ApiResponse<PlayerStats> = {
      success: true,
      data: player.stats,
    };

    cacheService.set(cacheKey, response, 600);
    res.json(response);
  } catch (error) {
    res.status(500).json({
      success: false,
      error: 'Failed to fetch player stats',
    });
  }
});

/**
 * GET /players/:id/fantasyScore - Get calculated fantasy score
 * Optional: formula in body for custom scoring
 */
app.get('/players/:id/fantasyScore', (req: Request, res: Response) => {
  try {
    const cacheKey = `player:fantasy:${req.params.id}`;
    const cached = cacheService.get<ApiResponse<{ fantasyScore: number; avgScore: number }>>(cacheKey);
    if (cached) {
      return res.json(cached);
    }

    const player = getMockPlayerById(req.params.id);
    if (!player) {
      return res.status(404).json({
        success: false,
        error: 'Player not found',
      });
    }

    const formula = (req.query.formula as any) || DEFAULT_SCORING_FORMULA;

    const response: ApiResponse<{ fantasyScore: number; avgScore: number }> = {
      success: true,
      data: {
        fantasyScore: calculateFantasyScore(player.stats, formula),
        avgScore: calculateAverageScore(player.stats, formula),
      },
    };

    cacheService.set(cacheKey, response, 600);
    res.json(response);
  } catch (error) {
    res.status(500).json({
      success: false,
      error: 'Failed to calculate fantasy score',
    });
  }
});

/**
 * GET /topPlayers - Get leaderboard of top players
 * Query params: limit, position (optional)
 */
app.get('/topPlayers', (req: Request, res: Response) => {
  try {
    const limit = Math.min(parseInt(req.query.limit as string) || 10, 50);
    const cacheKey = `topPlayers:${limit}:${req.query.position || 'all'}`;
    const cached = cacheService.get<ApiResponse<Player[]>>(cacheKey);
    if (cached) {
      return res.json(cached);
    }

    let players = getTopMockPlayers(limit);

    if (req.query.position) {
      const position = req.query.position as string;
      if (['GK', 'DEF', 'MID', 'FWD'].includes(position)) {
        players = players.filter((p) => p.position === position);
      }
    }

    const response: ApiResponse<Player[]> = {
      success: true,
      data: players,
    };

    cacheService.set(cacheKey, response, 600);
    res.json(response);
  } catch (error) {
    res.status(500).json({
      success: false,
      error: 'Failed to fetch top players',
    });
  }
});

/**
 * POST /validate-team - Validate team composition
 * Body: { composition: { goalkeepers, defenders, midfielders, forwards } }
 */
app.post('/validate-team', (req: Request, res: Response) => {
  try {
    const { composition } = req.body as { composition: TeamComposition };

    if (!composition) {
      return res.status(400).json({
        success: false,
        error: 'Team composition required',
      });
    }

    const validation = validateTeamComposition(composition);

    res.json({
      success: true,
      data: validation,
    });
  } catch (error) {
    res.status(500).json({
      success: false,
      error: 'Failed to validate team',
    });
  }
});

/**
 * POST /calculate-team-score - Calculate total fantasy score for a team
 * Body: { playerIds: string[] }
 */
app.post('/calculate-team-score', (req: Request, res: Response) => {
  try {
    const { playerIds } = req.body as { playerIds: string[] };

    if (!playerIds || !Array.isArray(playerIds)) {
      return res.status(400).json({
        success: false,
        error: 'Player IDs array required',
      });
    }

    const allPlayers = getMockPlayers();
    const teamPlayers = playerIds
      .map((id) => allPlayers.find((p) => p.id === id))
      .filter((p): p is Player => p !== undefined);

    if (teamPlayers.length !== playerIds.length) {
      return res.status(400).json({
        success: false,
        error: 'One or more players not found',
      });
    }

    const totalScore = teamPlayers.reduce((sum, p) => sum + (p.fantasyScore || 0), 0);
    const avgScore = totalScore / teamPlayers.length;

    // Count composition
    const composition: TeamComposition = {
      goalkeepers: teamPlayers.filter((p) => p.position === 'GK').length,
      defenders: teamPlayers.filter((p) => p.position === 'DEF').length,
      midfielders: teamPlayers.filter((p) => p.position === 'MID').length,
      forwards: teamPlayers.filter((p) => p.position === 'FWD').length,
    };

    const validation = validateTeamComposition(composition);

    res.json({
      success: true,
      data: {
        totalScore,
        avgScore,
        playerCount: teamPlayers.length,
        composition,
        isValid: validation.valid,
        errors: validation.errors,
      },
    });
  } catch (error) {
    res.status(500).json({
      success: false,
      error: 'Failed to calculate team score',
    });
  }
});

/**
 * POST /custom-scoring - Calculate scores with custom formula
 * Body: { stats: PlayerStats, formula: ScoringFormula }
 */
app.post('/custom-scoring', (req: Request, res: Response) => {
  try {
    const { stats, formula } = req.body as {
      stats: PlayerStats;
      formula: ScoringFormula;
    };

    if (!stats || !formula) {
      return res.status(400).json({
        success: false,
        error: 'Stats and formula required',
      });
    }

    const fantasyScore = calculateFantasyScore(stats, formula);
    const avgScore = calculateAverageScore(stats, formula);

    res.json({
      success: true,
      data: {
        fantasyScore,
        avgScore,
        formula,
      },
    });
  } catch (error) {
    res.status(500).json({
      success: false,
      error: 'Failed to calculate custom scoring',
    });
  }
});

/**
 * POST /clear-cache - Clear the cache (admin endpoint)
 */
app.post('/clear-cache', (req: Request, res: Response) => {
  try {
    cacheService.clear();
    res.json({
      success: true,
      message: 'Cache cleared',
    });
  } catch (error) {
    res.status(500).json({
      success: false,
      error: 'Failed to clear cache',
    });
  }
});

// Start server
app.listen(PORT, () => {
  console.log(`üöÄ Fantasy Soccer API running on http://localhost:${PORT}`);
  console.log(`üìö GET http://localhost:${PORT}/players - List all players`);
  console.log(`üîù GET http://localhost:${PORT}/topPlayers?limit=10 - Top players`);
  console.log(`‚ù§Ô∏è GET http://localhost:${PORT}/health - Health check`);
});

export default app;

// Player Statistics
export interface PlayerStats {
  goals: number;
  assists: number;
  cleanSheets: number;
  yellowCards: number;
  redCards: number;
  appearances: number;
  avgRating?: number;
}

// Player
export interface Player {
  id: string;
  name: string;
  club: string;
  position: 'GK' | 'DEF' | 'MID' | 'FWD';
  photo: string;
  stats: PlayerStats;
  fantasyScore?: number;
  market_value?: number;
}

// Fantasy Team
export interface FantasyTeam {
  id?: string;
  name: string;
  players: Player[];
  totalScore: number;
}

// Scoring Formula
export interface ScoringFormula {
  goalsMultiplier: number;
  assistsMultiplier: number;
  cleanSheetsMultiplier: number;
  yellowCardsPenalty: number;
  redCardsPenalty: number;
}

// Team Composition
export interface TeamComposition {
  goalkeepers: number;
  defenders: number;
  midfielders: number;
  forwards: number;
}

// Team Validation
export interface TeamValidation {
  valid: boolean;
  errors: string[];
}

import axios, { AxiosInstance } from 'axios';
import { Player, PlayerStats, ScoringFormula, TeamComposition, TeamValidation } from '../types';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

class ApiClient {
  private client: AxiosInstance;

  constructor() {
    this.client = axios.create({
      baseURL: API_BASE_URL,
      timeout: 10000,
    });
  }

  // Players endpoints
  async getPlayers(position?: string, sortBy?: string): Promise<Player[]> {
    const response = await this.client.get('/players', {
      params: { position, sortBy },
    });
    return response.data.data || [];
  }

  async getPlayerById(id: string): Promise<Player> {
    const response = await this.client.get(`/players/${id}`);
    return response.data.data;
  }

  async getPlayerStats(id: string): Promise<PlayerStats> {
    const response = await this.client.get(`/players/${id}/stats`);
    return response.data.data;
  }

  async getPlayerFantasyScore(id: string): Promise<{ fantasyScore: number; avgScore: number }> {
    const response = await this.client.get(`/players/${id}/fantasyScore`);
    return response.data.data;
  }

  async getTopPlayers(limit: number = 10, position?: string): Promise<Player[]> {
    const response = await this.client.get('/topPlayers', {
      params: { limit, position },
    });
    return response.data.data || [];
  }

  // Team validation
  async validateTeam(composition: TeamComposition): Promise<TeamValidation> {
    const response = await this.client.post('/validate-team', { composition });
    return response.data.data;
  }

  async calculateTeamScore(playerIds: string[]): Promise<{
    totalScore: number;
    avgScore: number;
    playerCount: number;
    composition: TeamComposition;
    isValid: boolean;
    errors: string[];
  }> {
    const response = await this.client.post('/calculate-team-score', { playerIds });
    return response.data.data;
  }

  // Custom scoring
  async calculateCustomScore(
    stats: PlayerStats,
    formula: ScoringFormula
  ): Promise<{ fantasyScore: number; avgScore: number; formula: ScoringFormula }> {
    const response = await this.client.post('/custom-scoring', { stats, formula });
    return response.data.data;
  }

  // Health check
  async healthCheck(): Promise<boolean> {
    try {
      const response = await this.client.get('/health');
      return response.data.success;
    } catch {
      return false;
    }
  }
}

export const apiClient = new ApiClient();

import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ApiProvider } from './context/ApiContext';
import { TeamProvider } from './context/TeamContext';
import Dashboard from './pages/Dashboard';
import PlayerComparison from './pages/PlayerComparison';
import Navigation from './components/Navigation';
import './App.css';

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [apiHealth, setApiHealth] = useState(false);

  useEffect(() => {
    // Check API health on mount
    const checkHealth = async () => {
      try {
        const response = await fetch('http://localhost:5000/health', {
          method: 'GET',
        });
        setApiHealth(response.ok);
      } catch (error) {
        console.error('API health check failed:', error);
        setApiHealth(false);
      } finally {
        setIsLoading(false);
      }
    };

    checkHealth();
  }, []);

  return (
    <ApiProvider>
      <TeamProvider>
        <Router>
          <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900">
            {!apiHealth && !isLoading && (
              <div className="bg-red-500 text-white p-4 text-center">
                ‚ö†Ô∏è API server is not running. Please start the backend server on port 5000.
              </div>
            )}

            {isLoading ? (
              <div className="flex items-center justify-center h-screen">
                <div className="text-center">
                  <div className="inline-block animate-spin rounded-full h-12 w-12 border-b-2 border-white"></div>
                  <p className="mt-4 text-white text-xl">Loading...</p>
                </div>
              </div>
            ) : (
              <>
                <Navigation />
                <Routes>
                  <Route path="/" element={<Dashboard />} />
                  <Route path="/compare" element={<PlayerComparison />} />
                </Routes>
              </>
            )}
          </div>
        </Router>
      </TeamProvider>
    </ApiProvider>
  );
}

export default App;

/* Global styles for Fantasy Soccer Dashboard */

:root {
  --primary: #8b5cf6;
  --primary-dark: #7c3aed;
  --primary-light: #a78bfa;
  --secondary: #06b6d4;
  --success: #10b981;
  --danger: #ef4444;
  --warning: #f59e0b;
  --dark: #0f172a;
  --light: #f1f5f9;
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen',
    'Ubuntu', 'Cantarell', 'Fira Sans', 'Droid Sans', 'Helvetica Neue',
    sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  background: linear-gradient(135deg, #0f172a 0%, #1e1b4b 50%, #0f172a 100%);
  color: #e2e8f0;
}

/* Animations */
@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slideInLeft {
  from {
    opacity: 0;
    transform: translateX(-20px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes slideInRight {
  from {
    opacity: 0;
    transform: translateX(20px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes pulse {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.5;
  }
}

@keyframes scale {
  0%, 100% {
    transform: scale(1);
  }
  50% {
    transform: scale(1.05);
  }
}

.animate-fade-in {
  animation: fadeIn 0.3s ease-in-out;
}

.animate-slide-in-left {
  animation: slideInLeft 0.3s ease-in-out;
}

.animate-slide-in-right {
  animation: slideInRight 0.3s ease-in-out;
}

.animate-pulse-custom {
  animation: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
}

/* Gradient text */
.gradient-text {
  background: linear-gradient(135deg, #a78bfa 0%, #06b6d4 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

/* Scrollbar styling */
::-webkit-scrollbar {
  width: 8px;
}

