import React, { useState, useMemo } from 'react';
import { Player } from '../types';
import { getTeamComposition } from '../utils/calculations';
import { SearchBar } from '../components/SearchBar';
import { FilterBar } from '../components/FilterBar';
import { PlayersGrid } from '../components/PlayersGrid';
import { PlayersTable } from '../components/PlayersTable';
import { TeamSummary } from '../components/TeamSummary';
import { CompositionChart } from '../components/charts/CompositionChart';
import { ComparisonChart } from '../components/charts/ComparisonChart';
import { LayoutGrid, List, Trash2, Save } from 'lucide-react';

interface DashboardProps {
  allPlayers: Player[];
  teamPlayers: Player[];
  totalScore: number;
  onAddPlayer: (player: Player) => void;
  onRemovePlayer: (playerId: string) => void;
  isLoading?: boolean;
}

export default function Dashboard({
  allPlayers,
  teamPlayers,
  totalScore,
  onAddPlayer,
  onRemovePlayer,
  isLoading = false,
}: DashboardProps) {
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedPosition, setSelectedPosition] = useState<string | undefined>();
  const [sortBy, setSortBy] = useState<string>('fantasyScore');
  const [viewMode, setViewMode] = useState<'grid' | 'table'>('grid');

  const filteredPlayers = useMemo(() => {
    let result = allPlayers;

    // Search filter
    if (searchQuery) {
      const query = searchQuery.toLowerCase();
      result = result.filter(
        (p) =>
          p.name.toLowerCase().includes(query) ||
          p.club.toLowerCase().includes(query)
      );
    }

    // Position filter
    if (selectedPosition) {
      result = result.filter((p) => p.position === selectedPosition);
    }

    // Sort
    result.sort((a, b) => {
      const aScore = a.fantasyScore || 0;
      const bScore = b.fantasyScore || 0;

      switch (sortBy) {
        case 'fantasyScore':
          return bScore - aScore;
        case 'goals':
          return b.stats.goals - a.stats.goals;
        case 'assists':
          return b.stats.assists - a.stats.assists;
        case 'appearances':
          return b.stats.appearances - a.stats.appearances;
        default:
          return bScore - aScore;
      }
    });

    return result;
  }, [allPlayers, searchQuery, selectedPosition, sortBy]);

  const composition = getTeamComposition(teamPlayers);
  const selectedPlayerIds = teamPlayers.map((p) => p.id);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
      {/* Header */}
      <header className="bg-gradient-to-r from-blue-600 to-indigo-600 text-white shadow-lg">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <h1 className="text-4xl font-bold mb-2">‚öΩ Fantasy Soccer Dashboard</h1>
          <p className="text-blue-100">Build your dream team and compete on the leaderboard</p>
        </div>
      </header>

      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Team Summary */}
        <section className="mb-8">
          <h2 className="text-2xl font-bold text-gray-900 mb-4">Your Team</h2>
          <TeamSummary players={teamPlayers} totalScore={totalScore} composition={composition} />
        </section>

        {/* Charts Section */}
        {teamPlayers.length > 0 && (
          <section className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
            <div className="bg-white p-6 rounded-lg shadow-md">
              <CompositionChart composition={composition} />
            </div>
            {teamPlayers.length >= 3 && (
              <div className="bg-white p-6 rounded-lg shadow-md">
                <ComparisonChart players={teamPlayers} maxPlayers={6} />
              </div>
            )}
          </section>
        )}

        {/* Players Section */}
        <section className="mb-8">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-2xl font-bold text-gray-900">Available Players</h2>
            <div className="flex gap-2">
              <button
                onClick={() => setViewMode('grid')}
                className={`p-2 rounded-lg transition-colors ${
                  viewMode === 'grid'
                    ? 'bg-blue-500 text-white'
                    : 'bg-white text-gray-600 border border-gray-300'
                }`}
                title="Grid view"
              >
                <LayoutGrid className="w-5 h-5" />
              </button>
              <button
                onClick={() => setViewMode('table')}
                className={`p-2 rounded-lg transition-colors ${
                  viewMode === 'table'
                    ? 'bg-blue-500 text-white'
                    : 'bg-white text-gray-600 border border-gray-300'
                }`}
                title="Table view"
              >
                <List className="w-5 h-5" />
              </button>
            </div>
          </div>

          {/* Search and Filter */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
            <SearchBar onSearch={setSearchQuery} />
          </div>

          <FilterBar
            positions={['GK', 'DEF', 'MID', 'FWD']}
            selectedPosition={selectedPosition}
            onPositionChange={setSelectedPosition}
            sortBy={sortBy}
            onSortChange={setSortBy}
          />

          {/* Players Grid or Table */}
          <div className="mt-6">
            {viewMode === 'grid' ? (
              <PlayersGrid
                players={filteredPlayers}
                selectedPlayerIds={selectedPlayerIds}
                onAddPlayer={onAddPlayer}
                isLoading={isLoading}
              />
            ) : (
              <PlayersTable
                players={filteredPlayers}
                selectedPlayerIds={selectedPlayerIds}
                onAddPlayer={onAddPlayer}
                onRemovePlayer={onRemovePlayer}
                showActions={true}
              />
            )}
          </div>
        </section>

        {/* Team Management */}
        {teamPlayers.length > 0 && (
          <section className="mb-8">
            <h2 className="text-2xl font-bold text-gray-900 mb-4">Team Players</h2>
            <PlayersTable
              players={teamPlayers}
              selectedPlayerIds={selectedPlayerIds}
              onRemovePlayer={onRemovePlayer}
              showActions={true}
            />
          </section>
        )}
      </main>

      {/* Footer */}
      <footer className="bg-gray-900 text-gray-300 py-8 mt-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <p>¬© 2024 Fantasy Soccer Dashboard | Built with React, TypeScript & Node.js</p>
        </div>
      </footer>
    </div>
  );
}

