.dashboard {
  min-height: 100vh;
  padding-bottom: 40px;
}

.dashboard-header {
  text-align: center;
  padding: 40px 24px;
  background: linear-gradient(135deg, rgba(139, 92, 246, 0.1) 0%, rgba(6, 182, 212, 0.1) 100%);
  border-bottom: 1px solid rgba(148, 163, 184, 0.1);
  animation: fadeIn 0.5s ease-in-out;
}

.page-title {
  font-size: 32px;
  font-weight: 700;
  margin-bottom: 8px;
  background: linear-gradient(135deg, #a78bfa 0%, #06b6d4 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.page-subtitle {
  font-size: 16px;
  color: rgba(226, 232, 240, 0.7);
}

.dashboard-container {
  max-width: 1400px;
  margin: 0 auto;
  padding: 24px;
  display: grid;
  grid-template-columns: 1fr 320px;
  gap: 24px;
}

.dashboard-main {
  display: flex;
  flex-direction: column;
  gap: 24px;
}

/* Controls Section */
.controls-section {
  display: flex;
  flex-direction: column;
  gap: 16px;
}

.controls-row {
  display: flex;
  gap: 16px;
  flex-wrap: wrap;
  align-items: center;
}

.controls-group {
  display: flex;
  gap: 12px;
  flex-wrap: wrap;
  align-items: center;
}

.filter-select {
  padding: 10px 12px;
  background: rgba(15, 23, 42, 0.5);
  border: 1px solid rgba(148, 163, 184, 0.2);
  color: #e2e8f0;
  border-radius: 8px;
  font-size: 14px;
  cursor: pointer;
  transition: all 0.2s ease;
}

.filter-select:hover,
.filter-select:focus {
  border-color: #8b5cf6;
  background: rgba(15, 23, 42, 0.7);
  outline: none;
}

/* View Controls */
.view-controls {
  display: flex;
  gap: 8px;
  margin-left: auto;
}

.view-btn {
  width: 40px;
  height: 40px;
  border: 1px solid rgba(148, 163, 184, 0.2);
  background: rgba(15, 23, 42, 0.5);
  color: rgba(226, 232, 240, 0.6);
  border-radius: 8px;
  cursor: pointer;
  font-size: 18px;
  transition: all 0.2s ease;
  display: flex;
  align-items: center;
  justify-content: center;
}

.view-btn:hover {
  border-color: #8b5cf6;
  background: rgba(139, 92, 246, 0.1);
  color: #a78bfa;
}

.view-btn.active {
  background: linear-gradient(135deg, #8b5cf6 0%, #7c3aed 100%);
  color: white;
  border-color: #8b5cf6;
}

/* Players Grid */
.players-grid {
  display: grid;
  gap: 16px;
  animation: fadeIn 0.3s ease-in-out;
}

/* Players Table */
.players-table-container {
  overflow-x: auto;
  border-radius: 12px;
  border: 1px solid rgba(148, 163, 184, 0.1);
  background: rgba(15, 23, 42, 0.5);
  backdrop-filter: blur(10px);
  animation: fadeIn 0.3s ease-in-out;
}

.players-table {
  width: 100%;
  border-collapse: collapse;
  font-size: 14px;
}

.players-table thead {
  background: rgba(139, 92, 246, 0.1);
  border-bottom: 2px solid rgba(148, 163, 184, 0.1);
}

.players-table th {
  padding: 16px;
  text-align: left;
  font-weight: 600;
  color: #a78bfa;
  text-transform: uppercase;
  font-size: 12px;
  letter-spacing: 0.5px;
}

.players-table tbody tr {
  border-bottom: 1px solid rgba(148, 163, 184, 0.05);
  transition: background 0.2s ease;
}

.players-table tbody tr:hover {
  background: rgba(139, 92, 246, 0.05);
}

.player-row td {
  padding: 16px;
  vertical-align: middle;
}

.player-name {
  display: flex;
  align-items: center;
  gap: 12px;
  font-weight: 500;
  color: #e2e8f0;
}

.player-avatar {
  width: 40px;
  height: 40px;
  border-radius: 8px;
  object-fit: cover;
  border: 1px solid rgba(148, 163, 184, 0.1);
}

.fantasy-score {
  font-weight: 700;
  background: linear-gradient(135deg, #a78bfa 0%, #06b6d4 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

/* Loading & Empty States */
.loading-container {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 60px 24px;
  color: rgba(226, 232, 240, 0.6);
}

.spinner {
  display: inline-block;
  width: 40px;
  height: 40px;
  border: 3px solid rgba(148, 163, 184, 0.2);
  border-top-color: #8b5cf6;
  border-radius: 50%;
  animation: spin 0.8s linear infinite;
  margin-bottom: 16px;
}

@keyframes spin {
  to {
    transform: rotate(360deg);
  }
}

.empty-state {
  text-align: center;
  padding: 60px 24px;
  color: rgba(226, 232, 240, 0.5);
}

.empty-state p {
  font-size: 18px;
  margin-bottom: 8px;
}

.empty-state small {
  font-size: 14px;
  display: block;
}

/* Sidebar */
.dashboard-sidebar {
  display: flex;
  flex-direction: column;
  gap: 24px;
  height: fit-content;
  position: sticky;
  top: 80px;
}

/* Responsive Design */
@media (max-width: 1024px) {
  .dashboard-container {
    grid-template-columns: 1fr;
  }

  .dashboard-sidebar {
    position: static;
  }

  .controls-row {
    flex-direction: column;
    align-items: stretch;
  }

  .controls-group {
    flex-direction: column;
  }

  .filter-select {
    width: 100%;
  }

  .view-controls {
    margin-left: 0;
    width: 100%;
    justify-content: space-between;
  }

  .view-btn {
    flex: 1;
  }
}

@media (max-width: 768px) {
  .dashboard-header {
    padding: 24px 16px;
  }

  .page-title {
    font-size: 24px;
  }

  .page-subtitle {
    font-size: 14px;
  }

  .dashboard-container {
    padding: 16px;
    gap: 16px;
  }

  .players-table {
    font-size: 12px;
  }

  .players-table th,
  .players-table td {
    padding: 12px 8px;
  }

  .player-avatar {
    width: 32px;
    height: 32px;
  }

  .player-name span {
    display: none;
  }

  .view-btn {
    width: 32px;
    height: 32px;
    font-size: 14px;
  }
}

@media (max-width: 480px) {
  .dashboard-container {
    padding: 12px;
  }

  .page-title {
    font-size: 20px;
  }

  .controls-row {
    gap: 8px;
  }

  .players-grid.grid-3 {
    grid-template-columns: 1fr;
  }

  .players-table {
    font-size: 11px;
  }
}

import React, { useState } from 'react';
