        {/* More Stats */}
        <div className="grid grid-cols-2 gap-2 mb-3 text-center text-xs">
          <div className="p-2 bg-green-50 rounded">
            <p className="text-gray-600">Clean</p>
            <p className="font-bold text-green-600">{player.stats.cleanSheets}</p>
          </div>
          <div className="p-2 bg-red-50 rounded">
            <p className="text-gray-600">Cards</p>
            <p className="font-bold text-red-600">{player.stats.yellowCards + player.stats.redCards}</p>
          </div>
        </div>

        {/* Action Buttons */}
        {showActions && (
          <div className="flex gap-2">
            {onAdd && !isSelected && (
              <button
                onClick={onAdd}
                className="flex-1 bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-3 rounded-lg transition-colors flex items-center justify-center gap-1"
              >
                <Plus className="w-4 h-4" />
                Add
              </button>
            )}
            {onRemove && isSelected && (
              <button
                onClick={onRemove}
                className="flex-1 bg-red-500 hover:bg-red-600 text-white font-semibold py-2 px-3 rounded-lg transition-colors flex items-center justify-center gap-1"
              >
                <Trash2 className="w-4 h-4" />
                Remove
              </button>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

